// WAP to print the squares of 1st 10 odd numbers
package src.practiceprograms;

public class SquaresOfOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Squares of 1st 10 odd numbers:");

		for(int i=1; i<=10; i++) {
				System.out.println(((2*i)-1) * ((2*i)-1));
		}
	}
}
